import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPopular } from "../../store/film/filmSlice";
import './dashboard.css';


function Dashboard() {

    const dispatch = useDispatch();
    const { film } = useSelector((state) => state)
    const [popularMovie, setPopularMovie] = useState([]);
    const imgUrl = "https://image.tmdb.org/t/p/w500";

    useEffect(() => {
        dispatch(getPopular())
    }, []);

    useEffect(() => {
        if (film.isSuccess) {
            setPopularMovie(film.data.results)
            console.log(film)
        }
    }, [film])

    useEffect(() => {
        console.log(popularMovie)
    }, [popularMovie])

    return (
        <div className="dashContainer">
            <h1>Top film</h1>
            <a href="">
                <div className="TopRanking" >
                    {popularMovie.map((e, i) => {
                        return (
                            <div key={i} className="RankingCard">
                                <h2>{e.title}</h2>
                                <img src={imgUrl + e.poster_path} alt="Movie Poster" />
                                <p>{e.overview}</p>
                            </div>
                        )
                    })}
                </div >
            </a>
            <h2>Top s√©rie</h2>
        </div>
    )
}

export default Dashboard
