import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDiscoverMovie } from "../../store/film/discoverSlice";
import { getDiscoverTV } from "../../store/serie/discoverSlice";
import './discover.css';

function Discover() {
    const dispatch = useDispatch();
    const { discoverFilm } = useSelector((state) => state);
    const { discoverTV } = useSelector((state) => state);
    const [stateDiscoverFilm, setDiscoverFilm] = useState([]);
    const [stateDiscoverTV, setDiscoverTV] = useState([]);
    const [allType, setAllType] = useState([])

    useEffect(() => {
        dispatch(getDiscoverMovie());
        dispatch(getDiscoverTV());
    }, [])

    useEffect(() => {
        if (discoverFilm.isSuccess & discoverTV.isSuccess) {
            setDiscoverFilm(discoverFilm.data.results);
            setDiscoverTV(discoverTV.data.results)
            const concat = [].concat(discoverTV, discoverFilm)
            console.log(concat)
            setAllType(concat);
            console.log(allType)
        }
    }, [])

    return (
        <div className="discoverContainer">
        </div >
    )
}

export default Discover
