import { createSlice } from "@reduxjs/toolkit";

export const FavoriSlice = createSlice({
    name: "favori",
    initialState: {
        favori: null,
    },
    reducers: {
        getFavori: (state, action) => {
            state.favori = action.payload
        }
    }
})

export default FavoriSlice;
export const { getFavori } = FavoriSlice.actions
