import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const getPopular = createAsyncThunk("film/getData")

const filmSlice = createSlice({
    name: "film",
    initialState: {
        data: [],
        isSuccess: false,
        message: "",
        loading: false,
    },
    reducers: {},
    extraReducers: () => { },
});

export default filmSlice;
