import { type } from "@testing-library/user-event/dist/type";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getFilter } from "../../API/getFilterMovie";
import { getFilterTV } from "../../API/getFilterTv";
import { getDiscoverMovie } from "../../store/film/discoverSlice";
import { getDiscoverTV } from "../../store/serie/discoverSlice";
import './discover.css';

function Discover() {
    const dispatch = useDispatch();
    const { discoverFilm } = useSelector((state) => state);
    const { discoverTV } = useSelector((state) => state);
    const { genreMovie } = useSelector((state) => state);
    const { genreTv } = useSelector((state) => state);
    const [stateDiscoverFilm, setDiscoverFilm] = useState([]);
    const [stateDiscoverTV, setDiscoverTV] = useState([]);
    const [allTypeOrFilter, setAllTypeOrFilter] = useState([])
    const imgUrl = "https://image.tmdb.org/t/p/w500";

    //Variables pour les filtres
    const [filmOrTv, setFilmOrTv] = useState([]);
    const [activeGenre, setActiveGenre] = useState(0);
    const [storeFilter, setStoreFilter] = useState([]);
    const [filter, setFilter] = useState([])

    useEffect(() => {
        dispatch(getDiscoverMovie());
        dispatch(getDiscoverTV());
        dispatch(getFilter());
        dispatch(getFilterTV())
    }, [])

    useEffect(() => {
        if (discoverFilm.isSuccess & discoverTV.isSuccess) {
            setDiscoverFilm(discoverFilm.data.results);
            setDiscoverTV(discoverTV.data.results)
            setAllTypeOrFilter([].concat(stateDiscoverFilm, stateDiscoverTV));
        }
    }, [discoverFilm])

    useEffect(() => {
        if (storeFilter.length == 0) {
            setFilter(allTypeOrFilter)
            console.log("Coucou")
        } else if (storeFilter === "tv") {
            setFilter(stateDiscoverTV)
            setFilmOrTv(genreTv.data.genres)
        } else if (storeFilter === "film") {
            setFilter(stateDiscoverFilm)
            setFilmOrTv(genreMovie.data.genres)
        }
        if (activeGenre != 0) {
            setFilter(allTypeOrFilter.filter((movie) => (movie.genre_ids.includes(activeGenre))))
        } else {
            setFilter(allTypeOrFilter)
        }
    }, [storeFilter, activeGenre])

    //Configuration des checkbox Filtre
    function handleClick(e) {
        if (e.target.value === "film") {
            setStoreFilter(e.target.value)
            setActiveGenre(Number(e.target.id))
        } else if (e.target.value === "tv") {
            setStoreFilter(e.target.value)
            setActiveGenre(Number(e.target.id))
        }
        setActiveGenre(Number(e.target.id))
    }

    return (
        <div className="discoverContainer">
            <div className="mainFilterBox">
                <button value="film" onClick={handleClick}>Film</button>
                <button value="tv" onClick={handleClick}>SÃ©rie</button>
                <div className="FilterBox">
                    {filmOrTv.map((e, i) => {
                        return (
                            <button key={i} id={e.id} onClick={handleClick}>{e.name}</button>
                        )
                    })}
                </div>
            </div>
            <div className="DiscoverFilm">
                {filter.map((e, i) => {
                    return (
                        <div key={i} className="DiscoverCard">
                            <h2>{e.title} {e.name}</h2>
                            <img src={imgUrl + e.poster_path} alt="Movie Poster" />
                        </div>
                    )
                })}
            </div>
        </div >
    )
}

export default Discover
